export default {
  el: "#app",

  data() {
    return {
      messageList: [{
        email: "",
        account: "",
        username: "",
        password: "",
        isLogin: false,
        isRegister: false
      }]
    };
  },

  methods: {
    login: function () {
      console.log('login'); // 前端校验

      if (this.account == '') {
        alert('请填写账户');
        return;
      }

      if (this.password == '') {
        alert('请填写密码');
        return;
      }

      var params = {
        account: this.account,
        password: this.password
      };
      var that = this;
      axios.post('http://localhost:3000/api/signin', params).then(function (response) {
        console.log(response);

        if (response.status == 200) {
          that.isLogin = true;
        }
      }).catch(function (error) {
        console.log(error);
        alert('该账户不存在或密码错误');
        that.account = '';
        that.password = '';
      });
    },
    name: 'SignIn',

    mounted() {
      console.log(this.$route);
    }

  }
};
